    const [isEdit,setIsEdit]=useState(false);
    const toggleIsEdit = ()=>setIsEdit(!isEdit);

    const [locatContent,setLocalContent]=useState("");

    const handleRemove = ()=>{
        if(window.confirm(`${id}번째 일기를 정말 삭제하시겠습니까?`)){
            onRemove(id)
        }
    }
