    }

    return(
        <div className="DiaryItem">
            <div className="info">
                <span>작성자 : {author} | 감정점수 : {emotion} </span>
                <span className="date">
                <br/>
                  {new Date(created_date).toLocaleString()}  
                </span>
            </div>
            <div className="content">
                {isEdit?<>
                    <textarea value={localContent} onChange={(e)=>setLocalContent}></textarea>
