import{useState} from "react"

const DiaryEditor = ()=>{
    //1.작성자
    //2.일기본문
    //3.감정점수
    //사용자의 입력을 리액트에서 핸들링하기위해선 state를 사용할 수 있음 

    const[author, setAuthor] = useState("임수빈"); 
    const[content,setContent] = useState("내용을 입력하세요")
    return(
        <div className="DiaryEditor">
            <h2>오늘의 일기</h2>
            <div>
                <input name="author_input" value={author} onChange={(e)=>{
                    console.log(e.target.value);
                    console.log(e.target.name);
                    setAuthor(e.target.value)
                }}>
                </input>
            </div>

            <div>
                <textarea name="content_area" value={content} onChange={(e)=>{
                    console.log(e.target.name)
                    console.log(e.target.value)
                    setContent(e.target.value)
                }}
                />
            </div>
        </div>
    )

}

export default DiaryEditor;
